import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  Typography, 
  Button, 
  TextField, 
  Select, 
  MenuItem, 
  FormControl, 
  InputLabel,
  Grid,
  Paper,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Divider
} from '@mui/material';
import { categories } from './data/gameData';
import { GameState, GameSettings, Player, Location } from './types/game';

const App: React.FC = () => {
  const [gameState, setGameState] = useState<GameState>({
    settings: {
      numberOfPlayers: 4,
      numberOfSpies: 1,
      timer: 8,
      category: categories[0]
    },
    players: [],
    isGameStarted: false,
    currentTime: 0
  });

  const [currentPlayerIndex, setCurrentPlayerIndex] = useState<number>(-1);
  const [isRevealPhase, setIsRevealPhase] = useState<boolean>(false);
  const [isCardVisible, setIsCardVisible] = useState<boolean>(false);
  const [isGameEnded, setIsGameEnded] = useState<boolean>(false);
  const [selectedRoles, setSelectedRoles] = useState<{ [key: string]: { [key: string]: boolean } }>({});

  // Initialize selected roles when category changes
  useEffect(() => {
    const newSelectedRoles: { [key: string]: { [key: string]: boolean } } = {};
    gameState.settings.category.locations.forEach(location => {
      newSelectedRoles[location.name] = {};
      location.roles.forEach(role => {
        newSelectedRoles[location.name][role] = true;
      });
    });
    setSelectedRoles(newSelectedRoles);
  }, [gameState.settings.category]);

  const handleRoleToggle = (locationName: string, role: string) => {
    setSelectedRoles(prev => ({
      ...prev,
      [locationName]: {
        ...prev[locationName],
        [role]: !prev[locationName][role]
      }
    }));
  };

  const startGame = () => {
    const { numberOfPlayers, numberOfSpies, category } = gameState.settings;

    // Filter locations based on selected roles
    const availableLocations = category.locations.map(location => ({
      ...location,
      roles: location.roles.filter(role => selectedRoles[location.name][role])
    })).filter(location => location.roles.length > 0);

    if (availableLocations.length === 0) {
      alert('Please select at least one role for at least one location!');
      return;
    }

    // Randomly select a location from available ones
    const randomLocation = availableLocations[Math.floor(Math.random() * availableLocations.length)];

    // Create players array
    const players: Player[] = [];

    // Assign spies
    const spyIndices = new Set<number>();
    while (spyIndices.size < numberOfSpies) {
      spyIndices.add(Math.floor(Math.random() * numberOfPlayers));
    }

    // Create all players
    for (let i = 0; i < numberOfPlayers; i++) {
      if (spyIndices.has(i)) {
        players.push({
          id: i + 1,
          role: "Spy",
          location: "Unknown",
          isSpy: true
        });
      } else {
        const availableRoles = randomLocation.roles;
        const randomRole = availableRoles[Math.floor(Math.random() * availableRoles.length)];
        players.push({
          id: i + 1,
          role: randomRole,
          location: randomLocation.name,
          isSpy: false
        });
      }
    }

    setGameState(prev => ({
      ...prev,
      players,
      isGameStarted: true,
      currentTime: prev.settings.timer * 60
    }));
    setIsRevealPhase(true);
    setCurrentPlayerIndex(0);
    setIsGameEnded(false);
  };

  useEffect(() => {
    let timer: NodeJS.Timeout;
    if (gameState.isGameStarted && !isRevealPhase && gameState.currentTime > 0) {
      timer = setInterval(() => {
        setGameState(prev => ({
          ...prev,
          currentTime: prev.currentTime - 1
        }));
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [gameState.isGameStarted, gameState.currentTime, isRevealPhase]);

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.code === 'Space' && isRevealPhase) {
        if (isCardVisible) {
          // Hide current card and move to next player
          setIsCardVisible(false);
          if (currentPlayerIndex < gameState.players.length - 1) {
            setCurrentPlayerIndex(prev => prev + 1);
          } else {
            // All cards have been shown, start the game
            setIsRevealPhase(false);
            setCurrentPlayerIndex(-1);
          }
        } else {
          // Show current card
          setIsCardVisible(true);
        }
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [isRevealPhase, currentPlayerIndex, isCardVisible, gameState.players.length]);

  const formatTime = (seconds: number): string => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const renderPlayerCard = (player: Player) => (
    <Paper sx={{ p: 2 }}>
      <Typography variant="h6">Player {player.id}</Typography>
      <Typography>Role: {player.role}</Typography>
      <Typography>Location: {player.location}</Typography>
      {player.isSpy && (
        <Typography color="error">You are the Spy!</Typography>
      )}
    </Paper>
  );

  const handleEndGame = () => {
    setIsGameEnded(true);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Spy Game
        </Typography>

        {!gameState.isGameStarted ? (
          <Box sx={{ mt: 4 }}>
            <Grid container spacing={3}>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="number"
                  label="Number of Players"
                  value={gameState.settings.numberOfPlayers}
                  onChange={(e) => setGameState(prev => ({
                    ...prev,
                    settings: {
                      ...prev.settings,
                      numberOfPlayers: parseInt(e.target.value)
                    }
                  }))}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="number"
                  label="Number of Spies"
                  value={gameState.settings.numberOfSpies}
                  onChange={(e) => setGameState(prev => ({
                    ...prev,
                    settings: {
                      ...prev.settings,
                      numberOfSpies: parseInt(e.target.value)
                    }
                  }))}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="number"
                  label="Timer (minutes)"
                  value={gameState.settings.timer}
                  onChange={(e) => setGameState(prev => ({
                    ...prev,
                    settings: {
                      ...prev.settings,
                      timer: parseInt(e.target.value)
                    }
                  }))}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel>Category</InputLabel>
                  <Select
                    value={gameState.settings.category.name}
                    label="Category"
                    onChange={(e) => {
                      const category = categories.find(c => c.name === e.target.value);
                      if (category) {
                        setGameState(prev => ({
                          ...prev,
                          settings: {
                            ...prev.settings,
                            category
                          }
                        }));
                      }
                    }}
                  >
                    {categories.map(category => (
                      <MenuItem key={category.name} value={category.name}>
                        {category.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <Typography variant="h6" gutterBottom>
                  Select Available Roles:
                </Typography>
                {gameState.settings.category.locations.map(location => (
                  <Box key={location.name} sx={{ mb: 3 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      {location.name}
                    </Typography>
                    <FormGroup>
                      {location.roles.map(role => (
                        <FormControlLabel
                          key={role}
                          control={
                            <Checkbox
                              checked={selectedRoles[location.name]?.[role] ?? true}
                              onChange={() => handleRoleToggle(location.name, role)}
                            />
                          }
                          label={role}
                        />
                      ))}
                    </FormGroup>
                    <Divider sx={{ mt: 2 }} />
                  </Box>
                ))}
              </Grid>
              <Grid item xs={12}>
                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={startGame}
                >
                  Start Game
                </Button>
              </Grid>
            </Grid>
          </Box>
        ) : (
          <Box sx={{ mt: 4 }}>
            {isRevealPhase ? (
              <>
                <Typography variant="h4" gutterBottom align="center">
                  Player {currentPlayerIndex + 1}'s Turn
                </Typography>
                <Typography variant="body1" gutterBottom align="center" color="text.secondary">
                  Press SPACE to {isCardVisible ? 'hide' : 'show'} card
                </Typography>
                {isCardVisible && renderPlayerCard(gameState.players[currentPlayerIndex])}
              </>
            ) : (
              <>
                <Typography variant="h4" gutterBottom align="center">
                  Time Remaining: {formatTime(gameState.currentTime)}
                </Typography>
                <Typography variant="h5" gutterBottom align="center" color="text.secondary">
                  Game in Progress
                </Typography>
                <Grid container spacing={2}>
                  {gameState.players.map(player => (
                    <Grid item xs={12} sm={6} md={4} key={player.id}>
                      <Paper sx={{ p: 2, textAlign: 'center' }}>
                        <Typography variant="h6">Player {player.id}</Typography>
                        <Typography>Role: Hidden</Typography>
                        <Typography>Location: Hidden</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
                <Button
                  fullWidth
                  variant="contained"
                  color="secondary"
                  onClick={handleEndGame}
                  sx={{ mt: 2 }}
                >
                  End Game
                </Button>
              </>
            )}
            {isGameEnded && (
              <Box sx={{ mt: 4 }}>
                <Typography variant="h4" gutterBottom align="center" color="error">
                  Game Over!
                </Typography>
                <Typography variant="h5" gutterBottom align="center">
                  The Spies were:
                </Typography>
                <Grid container spacing={2}>
                  {gameState.players.map(player => (
                    <Grid item xs={12} sm={6} md={4} key={player.id}>
                      {renderPlayerCard(player)}
                    </Grid>
                  ))}
                </Grid>
                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={() => setGameState(prev => ({ ...prev, isGameStarted: false }))}
                  sx={{ mt: 2 }}
                >
                  Play Again
                </Button>
              </Box>
            )}
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default App;
